#!/usr/bin/env node
// Minimal route generation script for testing
import fs from "node:fs";

console.log("ğŸ” Generating routes for performance testing...");

const seed = fs.existsSync("src/routes.seed.json")
  ? JSON.parse(fs.readFileSync("src/routes.seed.json", "utf8"))
  : ["/", "/dashboard", "/search", "/feed"];

const siteMapOverride = process.env.SITEMAP_CONTENT;
let routes = seed;

if (siteMapOverride) {
  console.log("ğŸ“„ Using sitemap content for route discovery");
  const urls = [...siteMapOverride.matchAll(/<loc>(.*?)<\/loc>/g)]
    .map(m => new URL(m[1]).pathname);
  const unique = Array.from(new Set(urls))
    .filter(p => p && p.startsWith("/"));
  if (unique.length) routes = unique;
}

fs.writeFileSync("routes.json", JSON.stringify(routes, null, 2));
console.log(`âœ… Generated ${routes.length} routes:`, routes);
console.log(`ğŸ“ Wrote routes to routes.json`);
