const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      refetchOnWindowFocus: false, // Reduce unnecessary network requests
      retry: 1,
      staleTime: 5 * 60 * 1000, // 5 minutes - keep data fresh but avoid excessive refetching
      gcTime: 10 * 60 * 1000, // 10 minutes garbage collection time
    },
    mutations: {
      retry: 1,
    },
  },
});

// Use async storage to prevent blocking main thread during hydration
const persister = createSyncStoragePersister({ 
  storage: window.localStorage, 
  key: 'wigg-rq',
  serialize: JSON.stringify,
  deserialize: JSON.parse,
});
