function toggle() {
  const newTheme = current === 'dark' ? 'light' : 'dark';

  // Use View Transitions API for smooth theme switching
  if ('startViewTransition' in document) {
    (document as any).startViewTransition(() => {
      setTheme(newTheme);
    });
  } else {
    setTheme(newTheme);
  }
}
