deleteMedia: async (id: string): Promise<DataLayerResponse<any>> => {
  try {
    const { data, error } = await supabase
      .from('media')
      .delete()
      .eq('id', id)
      .single();

    if (error) return handleError(error);
    return handleSuccess(data);
  } catch (error) {
    return handleError(error);
  }
}

