const { test, expect } = require('@playwright/test');
const fs = require('fs');
const path = require('path');

// Load routes from seed file or use defaults
function loadRoutes() {
  const routesPath = path.join(process.cwd(), 'routes.json');
  const seedPath = path.join(process.cwd(), 'src', 'routes.seed.json');

  if (fs.existsSync(routesPath)) {
    return JSON.parse(fs.readFileSync(routesPath, 'utf8'));
  } else if (fs.existsSync(seedPath)) {
    return JSON.parse(fs.readFileSync(seedPath, 'utf8'));
  } else {
    return ["/", "/dashboard", "/search", "/feed"];
  }
}

// Validate BASE_URL
function validateBaseUrl(url) {
  if (!url) {
    throw new Error('BASE_URL environment variable is required');
  }

  try {
    const parsed = new URL(url);
    if (!['http:', 'https:'].includes(parsed.protocol)) {
      throw new Error('BASE_URL must use http or https protocol');
    }
    return url;
  } catch (error) {
    throw new Error(`Invalid BASE_URL: ${error.message}`);
  }
}

async function crawlRoute(page, baseUrl, route) {
  console.log(`ðŸ“ Testing route: ${route}`);

  try {
    // Navigate to route
    const response = await page.goto(`${baseUrl}${route}`, {
      waitUntil: 'networkidle',
      timeout: 15000
    });

    // Verify successful response
    if (response.status() >= 400) {
      throw new Error(`HTTP ${response.status()}`);
    }

    console.log(`âœ… Route ${route} loaded successfully (${response.status()})`);

    // Wait for initial paint
    await page.waitForLoadState('domcontentloaded');

    // Light interactions to trigger INP/CLS measurements
    await page.mouse.move(100, 300);
    await page.mouse.wheel(0, 500);

    // Try to focus/tab through elements
    try {
      await page.keyboard.press('Tab');
      await page.waitForTimeout(500);
    } catch (e) {
      // Ignore tab errors
    }

    // Wait for metrics to settle
    await page.waitForTimeout(2000);

    // Navigate away to flush metrics
    await page.goto('about:blank');

    console.log(`âœ… Completed interactions for ${route}`);
    return { route, success: true, status: response.status() };

  } catch (error) {
    console.log(`âŒ Error testing ${route}:`, error.message);

    // Take screenshot on failure
    try {
      await page.screenshot({
        path: `error-${route.replace(/[^a-zA-Z0-9]/g, '_')}.png`,
        fullPage: true
      });
    } catch (screenshotError) {
      console.log('Could not capture screenshot:', screenshotError.message);
    }

    return { route, success: false, error: error.message };
  }
}

// Main test function
test('RUM crawl all routes', async ({ page }) => {
  const routes = loadRoutes();
  const baseUrl = validateBaseUrl(process.env.BASE_URL);
  const maxRoutes = parseInt(process.env.MAX_ROUTES || '5');

  console.log(`ðŸŽ¯ Testing ${Math.min(routes.length, maxRoutes)} routes on ${baseUrl}`);

  const results = [];
  const testRoutes = routes.slice(0, maxRoutes);

  for (let i = 0; i < testRoutes.length; i++) {
    const result = await crawlRoute(page, baseUrl, testRoutes[i]);
    results.push(result);
  }

  // Generate summary
  const successful = results.filter(r => r.success).length;
  const failed = results.filter(r => !r.success).length;

  console.log(`\nðŸ“Š Summary: ${successful} successful, ${failed} failed`);

  if (failed > 0) {
    console.log('âŒ Failed routes:');
    results.filter(r => !r.success).forEach(r => {
      console.log(`  - ${r.route}: ${r.error}`);
    });
  }

  // Expect at least one route to succeed
  expect(successful).toBeGreaterThan(0);
});
